import React, { useEffect } from 'react';
import {observer} from 'mobx-react';

import requests from '../../services/requests';

import './converter.scss';

const Converter = observer(({ converterStore }) => {
	// useEffect(() => {
	// 	requests
	// 		.getCurrencyValue('usd', 'uah')
	// 		.then(data => {
	// 			console.log(data.uah)
	// 			converterStore.setCurrencyDollar(data.uah)
	// 		})
	// 		.then(() => console.log('Data USD', converterStore.dollar))
	// 		.catch(error => {
	// 			console.error('Error fetching currency rates:', error)
	// 		})
	// }, [])

	useEffect(() => {
		console.log(converterStore.firstSelect)
	}, [converterStore.firstSelect]);

	return (
		<section className='converter'>
			<h1 className='converter__title'>Currency Converter</h1>
			<div className='converter__field'>
				<select
					name='first'
					className='converter__select'
					value={converterStore.firstSelect}
					onChange={(e) => converterStore.handleCurrencyFrom(e)}
				>
					<option value='uah'>UAH</option>
					<option value='usd'>USD</option>
					<option value='eur'>EUR</option>
				</select>
				<input
					type='number'
					className='converter__input'
					value={converterStore.firstInputCount}
				/>
			</div>
			<div className='converter__field'>
				<select
					name='second'
					className='converter__select'
					value={converterStore.secondSelect}
					onChange={(e) => converterStore.handleCurrencyTo(e.target.value)}
				>
					<option value='usd'>USD</option>
					<option value='uah'>UAH</option>
					<option value='eur'>EUR</option>
				</select>
				<input
					type='number'
					className='converter__input'
					value={converterStore.secondInputCount}
				/>
			</div>
		</section>
	)
})

export default Converter
