const requests = {
	getCurrencyList: async (baseCurrency, date = 'latest', minified = false) => {
		const format = minified ? 'min.json' : 'json'
		const endpoint = `currencies/${baseCurrency.toLowerCase()}.${format}`
		const apiUrl = `https://cdn.jsdelivr.net/gh/fawazahmed0/currency-api@1/${date}/${endpoint}`

		const response = await fetch(apiUrl)

		if (!response.ok) {
			throw new Error(`HTTP error! Status: ${response.status}`)
		}

		return await response.json()
	}
}

export default requests
