const requests = {
	getCurrencyRate: async () => {
		const response = await fetch(
			'https://api.privatbank.ua/p24api/pubinfo?exchange&json&coursid=5'
		)
		if (response.ok) {
			return await response.json()
		} else {
			throw new Error(response.status)
		}
	},
}

export default requests
