import {makeObservable, observable, action} from 'mobx';

class ConverterStore {
	dollar = 0; 
	euro = 0;

	constructor() {
		makeObservable(this, {
            objUSD: observable,
            objEUR: observable,
			setCurrencyValue: action
		})
	}

	setCurrencyValue(currency, data) {
        currency = data;
    }   
}

const converterStore = new ConverterStore();

export default converterStore
