class Requests {
    getCurrencyRate () {
        const response =  fetch('https://api.privatbank.ua/p24api/pubinfo?exchange&json&coursid=5');
        if(response.ok) {
            return  response.json();
        } else {
            throw new Error(response.status);
        }
    }
}

export default Requests;
